mapper.select:
select count(*)
from member
where member_id = #{targetId}

select *
from member
where member_id = #{memberId} and member_pass = #{memberPass}

select count(*)
from member
where member_nick = #{targetNick}

select member_pass
from member
where member_id = #{dupMemberId}

mapper.update:
update member
    set member_pass = #{memberPass}, member_nick = #{memberNick}, member_name = #{memberName}, member_birth = #{member},
        member_phone_number = #{memberPhoneNumber}, member_address = #{memberAddress}
where member_id = #{memberId}
